using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using System.Diagnostics;using static System.Convert;using Arithmetic.BinaryOperaion;namespace Arithmetic.UnaryOperation{    public class Reciprocal : IUnary    {        public Reciprocal(string expressionValue, string resultValue, string preResult) : base(expressionValue, resultValue, preResult)        {
        }        public override void ChangeExpression()        {            if (ExpressionValue == "" || ExpressionValue.EndsWith(" "))            {                Debug.WriteLine("结尾不是单目");                ExpressionValue += "1/(" + Tool.MaxContain(displayText) + ")";            }            else            {                Debug.WriteLine("结尾是单目");                string UnaryExpression = GetUnaryExpression().Trim();                string partExpression = ExpressionValue.Substring(0, binaryIndex);                ExpressionValue = partExpression + "1/(" + UnaryExpression + ")";            }        }        public override void ChangeResultValue()        {            ResultValue = Tool1.Calculate("reciprocal", displayText = ResultValue == "" ? PreResult : ResultValue);        }            }}