using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using static System.Convert;using System.Diagnostics;using Arithmetic.BinaryOperaion;namespace Arithmetic.UnaryOperation{    public class Square : IUnary    {        public Square(string expressionValue, string resultValue, string preResult) : base(expressionValue, resultValue, preResult)        {        }        public override void ChangeExpression()        {            if (ExpressionValue == "" || ExpressionValue.EndsWith(" ")) //结尾是双目
            {                ExpressionValue += "sqr(" + Tool.MaxContain(displayText) + ")";            }            else            {                string UnaryExpression = GetUnaryExpression().Trim();                string partExpression = ExpressionValue.Substring(0, binaryIndex);                ExpressionValue = partExpression + "sqr(" + UnaryExpression + ")";            }        }        public override void ChangeResultValue()        {            ResultValue = Tool1.Calculate("sqr", displayText = ResultValue == "" ? PreResult : ResultValue);        }
            }}